</template>

<script setup>
import { onMounted, ref } from 'vue'
import LoginBox from './components/LoginBox.vue'
import HeaderNavComp from "./components/HeaderNavComp.vue";

//back to top button event function
const backToTop = () => {
  document.body.scrollTop = 0;
  document.documentElement.scrollTop = 0;
};

//animation of titles
let fadeAndMove = [
  {
    opacity: 0,
    transform: `translateY(-20px)`
  },
  {
    opacity: 1,
    transform: `translateY(0px)`
  }
];

let expand = [
  {
    letterSpacing: "-0.5rem",
    opacity: 0
  },
  {
    letterSpacing: "initial",
    opacity: 1
  }
];

let titleTiming = {
  duration: 2000,
  easing: "ease-in-out"
};

const title = ref(null)
const subtitle = ref(null)
onMounted(()=>{
  const titleChange = title.value.animate(fadeAndMove, titleTiming);

  let subtitleTiming = {
    duration: titleChange.effect.getComputedTiming().duration / 2,
    easing: "ease-in-out"
  };

  const subTitleChange = subtitle.value.animate(expand, subtitleTiming);
  subTitleChange.pause();
  titleChange.pause();

  // stop animation and play when clicking the session

  // subTitleChange.pause();
  //
  // document.addEventListener("click", ()=>{
  //   if(subTitleChange.playState !== "finished") {
  //     subTitleChange.play();
  //   }
  // })

  const sections = document.querySelectorAll('section');
  const callback = (e) => {
    titleChange.play();
    subTitleChange.play();
  };

  const observer = new IntersectionObserver(callback);

  sections.forEach( section => {
    observer.observe( section );
  });
})

</script>

<style scoped lang="less">
  .resume-svg-container{
    transform: rotate(-15deg);
    animation: rotate-anime 3s ease-in-out infinite;
  }

  @keyframes rotate-anime{
    0%{
      transform-origin: 100% 100%;
      transform: rotate(-15deg);
    }
    50%{
      transform-origin: 100% 100%;
      transform: rotate(-5deg);
    }
    100%{
      transform-origin: 100% 100%;
      transform: rotate(-15deg);
    }
  }
</style>
